-- Add ESG indicators for SASB (Sustainability Accounting Standards Board)
INSERT INTO public.esg_indicators (framework_id, indicator_code, name, category, subcategory, description, unit_of_measurement, reporting_frequency, is_quantitative, materiality_level)
SELECT 
  (SELECT id FROM public.esg_frameworks WHERE name = 'Sustainability Accounting Standards Board'),
  code, name, category, subcategory, description, unit_of_measurement, reporting_frequency, is_quantitative, materiality_level
FROM (VALUES
  ('SASB-ENV-1', 'GHG Emissions - Scope 1', 'environmental', 'climate', 'Direct greenhouse gas emissions from owned or controlled sources', 'metric tons CO2e', 'annual', true, 'high'),
  ('SASB-ENV-2', 'GHG Emissions - Scope 2', 'environmental', 'climate', 'Indirect greenhouse gas emissions from purchased energy', 'metric tons CO2e', 'annual', true, 'high'),
  ('SASB-ENV-3', 'GHG Emissions - Scope 3', 'environmental', 'climate', 'All other indirect greenhouse gas emissions in value chain', 'metric tons CO2e', 'annual', true, 'medium'),
  ('SASB-ENV-4', 'Energy Consumption', 'environmental', 'energy', 'Total energy consumed from all sources', 'GJ', 'annual', true, 'high'),
  ('SASB-ENV-5', 'Renewable Energy Percentage', 'environmental', 'energy', 'Percentage of energy from renewable sources', '%', 'annual', true, 'medium'),
  ('SASB-ENV-6', 'Water Withdrawal', 'environmental', 'water', 'Total water withdrawn from all sources', 'm³', 'annual', true, 'high'),
  ('SASB-ENV-7', 'Water Discharge Quality', 'environmental', 'water', 'Quality of water discharged back to environment', 'text', 'annual', false, 'medium'),
  ('SASB-ENV-8', 'Waste Generated', 'environmental', 'waste', 'Total waste generated by organization', 'metric tons', 'annual', true, 'high'),
  ('SASB-ENV-9', 'Waste Diverted from Disposal', 'environmental', 'waste', 'Waste diverted through recycling, reuse, or recovery', 'metric tons', 'annual', true, 'medium'),
  ('SASB-ENV-10', 'Hazardous Waste', 'environmental', 'waste', 'Hazardous waste generated and disposed', 'metric tons', 'annual', true, 'high'),
  ('SASB-SOC-1', 'Employee Health & Safety Incidents', 'social', 'workforce', 'Total recordable incident rate (TRIR)', 'rate per 200,000 hours', 'annual', true, 'high'),
  ('SASB-SOC-2', 'Lost Time Injury Frequency Rate', 'social', 'workforce', 'Frequency of lost time injuries per million hours worked', 'rate per million hours', 'annual', true, 'high'),
  ('SASB-SOC-3', 'Employee Training Hours', 'social', 'workforce', 'Average training hours per employee', 'hours', 'annual', true, 'medium'),
  ('SASB-SOC-4', 'Employee Turnover Rate', 'social', 'workforce', 'Percentage of employees who left the organization', '%', 'annual', true, 'medium'),
  ('SASB-SOC-5', 'Gender Pay Gap', 'social', 'diversity', 'Difference in pay between male and female employees', '%', 'annual', true, 'medium'),
  ('SASB-SOC-6', 'Board Diversity', 'social', 'diversity', 'Diversity of board members by gender, age, ethnicity', '%', 'annual', true, 'medium'),
  ('SASB-SOC-7', 'Supply Chain Labor Standards', 'social', 'human rights', 'Compliance with labor standards in supply chain', 'text', 'annual', false, 'high'),
  ('SASB-SOC-8', 'Community Investment', 'social', 'community', 'Total value of community programs and charitable giving', 'currency', 'annual', true, 'medium'),
  ('SASB-GOV-1', 'Anti-Corruption Training', 'governance', 'ethics', 'Percentage of employees trained in anti-corruption policies', '%', 'annual', true, 'high'),
  ('SASB-GOV-2', 'Business Ethics Violations', 'governance', 'ethics', 'Number of confirmed incidents of corruption and bribery', 'count', 'annual', true, 'high'),
  ('SASB-GOV-3', 'Data Privacy Breaches', 'governance', 'privacy', 'Number of substantiated complaints regarding breaches of customer privacy', 'count', 'annual', true, 'high'),
  ('SASB-GOV-4', 'Customer Data Protection', 'governance', 'privacy', 'Measures taken to protect customer data and privacy', 'text', 'annual', false, 'high'),
  ('SASB-GOV-5', 'Product Quality Incidents', 'governance', 'quality', 'Number of incidents of non-compliance with product quality standards', 'count', 'annual', true, 'medium'),
  ('SASB-GOV-6', 'Supplier Sustainability Assessment', 'governance', 'supply chain', 'Percentage of suppliers assessed for sustainability criteria', '%', 'annual', true, 'medium')
) AS t(code, name, category, subcategory, description, unit_of_measurement, reporting_frequency, is_quantitative, materiality_level);

