  ('TCFD-STRAT-5', 'Climate Opportunities', 'environmental', 'opportunity', 'Climate-related opportunities identified by the organization', 'text', 'annual', false, 'medium'),
  ('TCFD-RISK-1', 'Climate Risk Management Process', 'governance', 'risk management', 'Processes for identifying and assessing climate-related risks', 'text', 'annual', false, 'high'),
  ('TCFD-RISK-2', 'Climate Risk Integration', 'governance', 'risk management', 'How climate risk processes are integrated into overall risk management', 'text', 'annual', false, 'high'),
  ('TCFD-METR-1', 'GHG Emissions Scope 1 & 2', 'environmental', 'metrics', 'Direct and indirect GHG emissions (Scope 1 and 2)', 'metric tons CO2e', 'annual', true, 'high'),
  ('TCFD-METR-2', 'GHG Emissions Scope 3', 'environmental', 'metrics', 'Value chain GHG emissions (Scope 3) if appropriate', 'metric tons CO2e', 'annual', true, 'medium'),
  ('TCFD-METR-3', 'Climate-Related Targets', 'environmental', 'targets', 'Targets used to manage climate-related risks and opportunities', 'text', 'annual', false, 'high'),
  ('TCFD-METR-4', 'Performance Against Targets', 'environmental', 'performance', 'Performance against climate-related targets', 'text', 'annual', false, 'high'),
